import React from 'react';
import {Line} from 'react-chartjs-2';

class ChartDataAll extends React.Component{

getchartData(){
  // Ajax calls here
  this.setState({
    chartData:{
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets:[
        {
          label:'Revenue',
          data:[
            1617,
            1881,
            1903,
            1606,
            2105,
            1950
          ],
          backgroundColor:[
            '#007bff'
          ],
          borderColor:'none',
          pointRadius:0,
          borderDash:[],
          lineTension: 0.3,
          borderWidth:0
        }
      ]
    },

    chartDataBar:{
      labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
      datasets:[
        {
          label:'Pool Depth',
          data:[
            1617200,
            1881121,
            1903932,
            1706677,
            2105099,
            1950333
          ],
          backgroundColor:[
            '#007bff',
            '#007bff',
            '#007bff',
            '#007bff',
            '#007bff',
            '#007bff'
          ],

        }
      ]
    },

    chartDataDough:{
      labels: [
        'Assets Sold',
        'Assents Owned'
      ],
      datasets: [{
        data: [30, 4],
        backgroundColor: [
        '#007bff',
        '#FFCE56'
        ],
        hoverBackgroundColor: [
        '#007bff',
        '#FFCE56'
        ]
      }]
    }
    
  });
}

}

export default ChartDataAll;
