import React from "react";
import UserInfo from "./UserInfo"; 
import MyAddBar from "./AddressBar.js"; 
import { DrizzleContext } from "@drizzle/react-plugin";
import { Drizzle } from "@drizzle/store";
import options from "./drizzleOptions";
import {  Loader } from 'rimble-ui';
import JoinButton from "./Buttons/JoinButton.js";

const drizzle = new Drizzle(options);

 const Address = () => {
  return (
    <DrizzleContext.Provider drizzle={drizzle}>
      <DrizzleContext.Consumer>
        {drizzleContext => {
          const {drizzle, drizzleState, initialized } = drizzleContext;
          
          if(!initialized) {
            return <Loader size="30px" color="white" />
          }

          const { accounts } = drizzleState;
          const account = drizzleState.accounts[0];
          const isMember = drizzle.contracts.UserStorage.methods.addresses(account).call();
          let button;

          if (isMember) {
              button =  <MyAddBar drizzleState={drizzleState} drizzle={drizzle} /> 
            } else {
              button = <JoinButton onClick={this.toggleRegisterModal} onClose={this.toggleRegisterModal}  />
            }
        
            return (
              <div>
                {button}
              </div>
            );
          

          
        
  
  }}
  
      </DrizzleContext.Consumer>
    </DrizzleContext.Provider>
);
  
}
export default Address;
