import React from "react";
import { Container, Row, CardFooter, Card, CardHeader  } from "shards-react";
import PoolInterestBar from "../charts/PoolInterestBar.js"

export default class InterestCard extends React.Component {

  constructor(){
    super();
    this.state = {
      chartData:{}
    }
  }

  UNSAFE_componentWillMount(){
    this.getchartData();
  }

  getchartData(){
    // Ajax calls here
    this.setState({
      chartDataBar:{
        labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
        datasets:[
          {
            label:'Pool Depth',
            data:[
              1617200,
              1881121,
              1903932,
              1706677,
              2105099,
              1950333
            ],
            backgroundColor:[
              '#007bff',
              '#007bff',
              '#007bff',
              '#007bff',
              '#007bff',
              '#007bff'
            ],

          }
        ]
      }
      
    });
  }
  render() {
      return (  
        <div className="mb-4 col-sm-12 col-md-12 col-lg">
          <Card className="stats-small card-small mx-auto">
            <div className="px-0 pb-0 pt-.5rem card-body">
              <Row className="d-flex px-3 pb-3">
                <div className="stats-small__data">
                  <span className="col-sm stat_small_card text-uppercase">Active Pool Interest Accrued</span>
                  <h6 className="col-sm stats-small__value count mt-1">$1,754</h6>
                </div>
                
                <div className="col-sm stats_small_data stats_percentage text-right align-items-center">
                  <div>&uarr; 3.1%</div>
                </div>
              </Row>
              <CardFooter className="bg-white border-top">
                <Row className="text-muted">
                  <div className="col-xs col-sm-3 text-left pool_footer_text">Total Accrued Interest</div>
                  <div className="col-xs col-sm-3 text-right pool_footer_text border-right">$29,354</div>
                  <div className="col-xs col-sm-3 text-left pool_footer_text">Pool Depth</div>
                  <div className="col-xs col-sm-3 text-right pool_footer_text">$1,234,559</div>
                </Row> 
              </CardFooter> 
              <CardFooter className="bg-white border-top">
                <Row>
                  <PoolInterestBar chartData={this.state.chartDataBar} legendPosition="bottom" />
                </Row> 
              </CardFooter>                                                     
            </div>
          </Card>
        </div>
      );
  }
}
