import React from "react";
import { DrizzleContext } from "@drizzle/react-plugin";
import { Drizzle } from "@drizzle/store";
//import drizzleOptions from "./drizzleOptions";
import EthAccounts from "./EthAccounts"; // Check out drizzle's react components at @drizzle/react-components

import UserStorage from "../artifacts/contracts/UserStorage.json";

const drizzleOptions = {
  contracts: [UserStorage],
  events: {
    SimpleStorage: ["CreateUser"],
  },
};

const drizzle = new Drizzle(drizzleOptions);

const MyApp = () => {
  return (
    <DrizzleContext.Provider drizzle={drizzle}>
      <DrizzleContext.Consumer>
        {drizzleContext => {
          const {drizzle, drizzleState, initialized} = drizzleContext;

          if(!initialized) {
            return "Loading..."
          }

          return (
            <EthAccounts drizzle={drizzle} drizzleState={drizzleState} />
            )
          }}
      </DrizzleContext.Consumer>
    </DrizzleContext.Provider>
  );
}

export default MyApp;
