import React from "react";
import { Row,Button, Container  } from "shards-react";
import { Link } from '../../../routes';
import ProposalEndDate from '../component-overview/ProposalCountdown.js';
import moment from 'moment';
import Moment from 'react-moment';




export default class ProposalBar extends React.Component {

  constructor(){
    super();
    this.state = {
      error:null,
      isLoaded:false,
      proposalEndTime:undefined,
      unixTimestamp:undefined
    }
  }


  componentDidMount() {
    this.setTime = setTime(() => {
    const proposalEndTime = moment.unix(1584878400)
    this.setState({ proposalEndTime });
  })
}

  // getpoolStartTime(){
  //   // Ajax calls here
  //   const unixTimestamp=1584878400;
  //   this.setState({
  //     proposalEndTime: moment.unix(1584878400),
  //     <Moment format="MM DD YYYY, hh:mm a" unix>{unixTimestamp}</Moment>
  //     moment.unix('1584922764'),<Moment format="MM DD YYYY, HH:mm a" unix>{1584922764}</Moment>,
  //   poolStartTimer: <Moment  unix>{1584758598}</Moment>
  //   });
  // }
 
  // timeTillDate="03 22 2020, 12:00 am" 
  // timeFormat="MM DD YYYY, HH:mm a" 
render() {
  const { proposalEndTime } = this.state;
    return (  

    <div className="mb-4 d-flex ">
      <div className="card">
        <div className="card-body">
          <Row className="justify-content-center">
          <div className="col d-flex justify-content-center">
            <h6>{this.state.proposalEndTime}</h6>  
          </div>  
          <div className="col d-flex justify-content-center">
            <ProposalEndDate  timeTillDate={this.state.proposalEndTime}  />
          </div>  
          <div className="col d-flex justify-content-center">
            
          </div>  
          </Row>
        </div>     
      </div>
        <style jsx>{` 
                  .card-text {margin-bottom:.1rem;font-family: Poppins;color:black;}
                  .card {width:70%;margin-bottom:0;}
                  .card-body {padding:.5rem;font-size:18px;font-weight:300;} 
                  .arrow {color:#17c671;font-size:21px;font-weight:900;}
                  `}  
        </style>
    </div>

        );
    }
}
