import React from 'react';
import Navbar from './Navbar.js';
import HeadOne from './Head.js';
import Footer from './Footer.js'
import { DrizzleContext } from "@drizzle/react-plugin"; 
import { ToastContainer } from 'react-toastify';   
import { Drizzle, generateStore } from "@drizzle/store";

import { generateContractsInitialState } from "@drizzle/store";

import drizzleOptions  from "../EthComponents/drizzleOptions";
import { contractEventSaga, joinTransactionSaga, appSagas } from '../middleware/capSagas';
import dappReducers  from '../middleware/reducers/transactionReducer.js';

import appReducerCombine from '../middleware/reducers/index.js';
import { Provider } from 'react-redux';

//const cappReducer = { appReducers }
const contractEventNotifier = contractEventSaga;
//const joinTransactionComplete = joinTransactionSaga;

const appMiddlewares = [ contractEventNotifier ];

const initialState = {
  contracts: generateContractsInitialState(drizzleOptions)
};


const store = generateStore(
  {
    initialState,
    //appReducerCombine,
    dappReducers,
  //drizzleOptions,
  appMiddlewares,
  appSagas,
  disableReduxDevTools: false 
})

const drizzle = new Drizzle(drizzleOptions, store);


export const Page = ({ children }) => (
  <div>
   
    <DrizzleContext.Provider drizzle={drizzle} >
    {console.log(drizzle)}
    <Provider store={store}>
     <HeadOne />

      <Navbar />  
      <script src="https://kit.fontawesome.com/bd21ab8235.js" crossOrigin="anonymous"></script>
    
      <main>
        <ToastContainer />
        {children}
      </main>
 
   
      <Footer />
    </Provider>  
    </DrizzleContext.Provider>
  </div>
) 

export const Center = ({ children }) => (
    <div>
      {children}   
    </div>
  )
