import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row } from "shards-react";
//import ExpiredNominationModal from './modals/ExpiredNominationModal';
//import CardFooterButton from './buttons/CardFooterBtn.js';
import NominationModal from './modals/NominationModal';
import ExpiredWDModal from './modals/ExpiredWDModal';
import SaleModal from './modals/SaleModal.js';
import SoldModal from './modals/SoldModal.js';
import PaidOutModal from './modals/PaidOutModal.js';
import PurchaseModal from './modals/PurchaseModal.js';
import NominationDetailsLink from './NominationDetailsLink.js';
import Moment from "react-moment";

const CardFooterButton = ({ props, userManaBalance, getSoldStatus, nftAddress, expiresAt, assetPrice, tokenId, orderStatus }) => {
  const [isStatus, setStatus] = useState();
  const [isStoredOrderId, setStoredOrderId] = useState();
  const [isOrderStatus, setOrderStatus] = useState();
  const [loading, setLoading] = useState(false);

  const oderStatusCall = (orderId) => {

    const myHeaders = new Headers();
    myHeaders.append("Content-Type", "application/json");
 
  
    const graphql = JSON.stringify({ query: 
      `{
        orders(where: {id:"${orderId}",category:parcel }) {
          status
        }
      }`
  })
  
  const targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'
  fetch(targetUrl, {
    method: "POST",
    headers:myHeaders,  
    body:graphql, variables:{},
    redirect: 'follow'
  })
    .then(res => res.json())
    .then( async (result) => {
        setOrderStatus(result.data.orders[0].status);
        setLoading(false); 
    },
    (error) => {
    return error;
    }
  )
  }


  const fetchProposals = async () => {
    const proposal = await props.drizzle.contracts.ProposalController.methods.deployedProposals(`${tokenId}`).call(); 

    if(proposal){
      const proposalAddress = await props.drizzle.contracts.ProposalController.methods.proposalTokenIdMap(`${tokenId}`).call();
      const proposalBalance = await props.drizzle.contracts.MANAToken.methods.balanceOf(`${proposalAddress}`).call();
      const propData = await props.drizzle.contracts.ProposalController.methods.getPropData(`${proposalAddress}`).call();
      const status = propData[0];

      //const status = await props.drizzle.contracts.ProposalController.methods.propStatus(`${proposalAddress}`).call(); 
      if(status === '4'){
        if(proposalBalance > assetPrice){
          const soldStatus = '6';
          getSoldStatus(proposalBalance, soldStatus);
          setStatus('6');
        }
        else{
          setStatus(status);
        }
      }
        else {
          setStatus(status);
        }
      }
    }

    useEffect(() => {
      fetchProposals();
    }, [tokenId]);

    return (
        <>
        {(() => {
        //switch (isStatus) {
        switch ('0') {
          //0-FUNDING
          case '0':
            return (
              <CardFooter className="p-0">
                 <ExpiredWDModal 
                  // nftAddress={nftAddress}
                  tokenId={tokenId}
                  // assetPrice={assetPrice}
                  // expiresAt={expiresAt}  
                  props={props} 
                  userManaBalance={userManaBalance}
                  orderStatus={orderStatus}
                />
              </CardFooter> 
            )
          //FUNDED
          case '1':
            return (
              <CardFooter className="p-0">
                <PurchaseModal 
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt} 
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter> 
            )
          //FOR_SALE
          case '4':
            return  (
              <CardFooter className="p-0">
                {console.log(isStatus)}
                <SaleModal 
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt} 
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter> 
            )
        //PAYINGOUT
          case '6':
            return  (
              <>
                <CardFooter className="p-0">
                  <SoldModal 
                    nftAddress={nftAddress}
                    tokenId={tokenId}
                    assetPrice={assetPrice}
                    expiresAt={expiresAt} 
                    props={props} 
                    userManaBalance={userManaBalance}
                  />
                </CardFooter> 
              </>
            )
            case '7':
              return  (
                <>
                  <CardFooter className="p-0">
                    <PaidOutModal
                      nftAddress={nftAddress}
                      tokenId={tokenId}
                      assetPrice={assetPrice}
                      expiresAt={expiresAt} 
                      props={props} 
                      userManaBalance={userManaBalance}
                    />
                  </CardFooter> 
                </>
            )
          case 'error':
            return'error'
          default:
            return (
              <CardFooter className="p-0">
                <NominationModal
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt} 
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter> 
            );
        }
      }
      )()}
    </>
    )
  }


const ExpiredNominations =  ({ orders, loading, props, userManaBalance, getSoldStatus }) => {
  if (loading) {
    return <Loader className="mt-2 mx-auto" size="30px" color="blue" />
  }
  return (  
    <Row className="mt-4">
      {orders.map(parcel => (
          <div key={parcel[0].id} className="mb-4 col-sm-12 col-md-12 col-lg-4">
            <Card>
              <CardHeader className="asset-header p-2 card-title text-center">Decentraland</CardHeader>
              <div>
                <div>
                  <CardTitle className="asset-title text-center">
                    <div className="current-price">Sale Cancelled</div>
                  </CardTitle>   
                </div>  
              </div>  
              <Row className="justify-content-center">
                <NominationDetailsLink tokenId={parcel[0].nft.parcel.tokenId} drizzle={props.drizzle} />
              </Row>
              <CardBody className="pb-.4rem;px-2">
                <Row className="asset-image align-items-center">
                  <img className="mx-auto" src={'https://api.decentraland.org/v1/parcels/' + parcel[0].nft.parcel.x + '/' + parcel[0].nft.parcel.y + '/map.png?height=260&width=260&size=12'} />
                </Row>
                <Row className="mt-2 justify-content-center">
                <div className="my-2 asset-card text-center card-text">Parcel:&nbsp;{parcel[0].nft.parcel.x},{parcel[0].nft.parcel.y}</div>
                </Row>
                <Row className="mt-3 justify-content-center">
                  <div className="mb-2 col-sm asset-card text-left card-text">End Date</div>
                  {/* <div className="mb-2 col-sm asset-card-time text-right card-text">
                    {(typeof(Number(parcel[0].nft.activeOrder.expiresAt)) == 'number')?
                      <Moment format="h:mma M/DD/YY">
                        {Number(parcel[0].nft.activeOrder.expiresAt)}
                      </Moment>       
                    :
                      'no end date'
                    }
                  </div>  */}
                </Row>  
              </CardBody> 
              <CardFooterButton 
                tokenId={parcel[0].nft.parcel.tokenId} 
                // expiresAt={parcel[0].nft.activeOrder.expiresAt} 
                // assetPrice={parcel[0].nft.activeOrder.price}
                // nftAddress={parcel[0].nft.activeOrder.nftAddress}
                props={props} 
                userManaBalance={userManaBalance}
                getSoldStatus={getSoldStatus}
                orderStatus={parcel[0].status}
                orderId={parcel[0].id} 
              />
            </Card>    
              {/* <CardFooterButton 
                 tokenId={parcel[0].nft.parcel.tokenId} 
                 expiresAt={parcel[0].nft.activeOrder.expiresAt} 
                 assetPrice={parcel[0].nft.activeOrder.price}
                 nftAddress={parcel[0].nft.activeOrder.nftAddress}
                 props={props} 
                 userManaBalance={userManaBalance}
                 getSoldStatus={getSoldStatus}
