import { combineReducers } from 'redux';
import { drizzleReducers } from '@drizzle/store';

const TX_DONE = 'TX_DONE'
const TX_PROCESSING = 'TX_PROCESSING'
const CHECK_METAMASK = "CHECK_METAMASK";
const CHECK_METAMASK_DONE = "CHECK_METAMASK_DONE";
const TX_ERROR_METAMASK = "TX_ERROR_METAMASK";
const TX_ERROR_METAMASK_DONE = "TX_ERROR_METAMASK_DONE";
const TX_SUCCESSFUL_UPDATE_UI = "TX_SUCCESSFUL_UPDATE_UI";
const CHECKING_TX_UI = "CHECKING_TX_UI";
const TX_PROCESSING_DONE = "TX_PROCESSING_DONE";
const GET_TOKEN_ID = 'GET_TOKEN_ID';


const initialState = {
  checkMetaMask: false,
  metaMaskReject: true,
  checkingTx: false,
  transactionProcessing:false,
  txSuccessful: false,  
  transactionComplete: false,
  tokenId: null
};   
 
function dappReducer(state = initialState, action) {
    switch (action.type) {
      case CHECK_METAMASK:
        return { ...state, checkMetaMask: true };
      case CHECK_METAMASK_DONE:
        return { ...state, checkMetaMask: false };
      case TX_ERROR_METAMASK:
        return { ...state, checkMetaMask: false, metaMaskReject: true };
      case TX_ERROR_METAMASK_DONE:
        return { ...state, metaMaskReject: false };
      case CHECKING_TX_UI:
        return { ...state, checkMetaMask: false, checkingTx: true };
      case TX_PROCESSING:
          return { ...state, transactionProcessing: true };
      case TX_PROCESSING_DONE:
        return { ...state, checkMetaMask: false, checkingTx: false,transactionProcessing: false, txSuccessful: true };
      case GET_TOKEN_ID:
          return { ...state, tokenId:action.tokenId};
      default:
        return state;
    }
  }   

export default combineReducers({ dappReducer: dappReducer, ...drizzleReducers });
