import React from 'react';
// import { ContractForm, AccountData, ContractData } from "@drizzle/react-components";
// import Web3 from "web3";

class ProposalAddress extends React.Component {
 state = { dataKey: null, drizzle:null };

componentDidMount() {
   const { drizzle, drizzleState, dataKey } = this.props;
                                              
  //const dataKey = contract.methods['profiles'].cacheCall(account);
  this.setState({ dataKey });

 }
  

 render() {

  const { ProposalController } = this.props.drizzleState.contracts;
  const displayData = ProposalController.getPropTokenIdMap[this.state.dataKey];
  const proposalAddress =  displayData && displayData.value;
    
   return (
    
   <div>Hello: {proposalAddress}
    {console.log({proposalAddress, displayData})}
   </div>

   )
 }
}

export default ProposalAddress;
