import React, { Component, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import ContributeInnerModal from './ContributeInnerModal.js';
import ProposalStorageAbi from '../../artifacts/contracts/ProposalStorageAbi.json';

   
class InitialContributeModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newAbi:null
    }
  }

  componentDidMount() {   
    const { getPropAbi } = this.props;
    const storageAbi = ProposalStorageAbi;
    this.setState({
      propsalAbi: storageAbi
    });
    getPropAbi(this.state.propsalAbi)
  }
  
  render() {
   //const newAbi = this.state.newAbi
    return (
    
    <ContributeInnerModal
      propAddress={this.props.propAddress} 
      userManaBalance={this.props.userManaBalance}
      assetPrice={this.props.assetPrice} 
      drizzleState={this.props.drizzleState} 
      drizzle={this.props.drizzle} 
      tokenId={this.props.tokenId} 
      props = {this.props}
      //newAbi= {newAbi}
    />
    )
  }
  

}

const mapStateToProps = (state) => ({
  //propStorageAbi: state.appReducers.newAbi
})

const mapDispatchToProps = (dispatch) => ({
  getPropAbi: (propsalAbi) =>  dispatch({ type: "GOT_PROP_ABI",proposalStorageAbi:propsalAbi })
});



export default connect(mapStateToProps, mapDispatchToProps)(InitialContributeModal);
