import { generateStore, EventActions, drizzleReducers } from '@drizzle/store'
import drizzleOptions from '../drizzleOptions'
import { toast } from 'react-toastify'
 //store.dispatch(addItem('Something'))


const contractEventNotifier = store => next => action => {
  if (action.type === EventActions.EVENT_FIRED) {
    const message = action.event.returnValues._message
    const display = `${message}`
 
    toast.success(display, { position: toast.POSITION.BOTTOM_RIGHT })
  }
  return next(action)
}

const joinTransactionNotifier = store => next => action => {
  if (action.type === 'TX_SUCCESSFUL') {
    const broadcast = action.txHash
    //const txStatus = action.event.event
    const message = action.receipt
    console.log({broadcast})
    const display = `${message}`
 
    toast.success(display, { position: toast.POSITION.BOTTOM_LEFT })
  }
  return next(action)
}

const appMiddlewares = [ contractEventNotifier, joinTransactionNotifier ]

const store = generateStore({
  drizzleOptions,
  appMiddlewares,
  disableReduxDevTools: false  // enable ReduxDevTools!
})

export default store;
