import React from "react";

import { Button, Form, Input } from "shards-react";
import Web3 from "web3";
import { toast } from 'react-toastify';
import JoinSubmitBtn from '../components/buttons/JoinSubmit';


const createUser = async (username) => {
  const { drizzle } = this.props;
  let state = drizzle.store.getState();
  const contract = drizzle.contracts.UserController;
  const uname = Web3.utils.fromAscii(this.state.newVal);
  contract.methods["createUser"].cacheSend(uname);
}


const InputForm = ({ title, value, onChange }) => (
  <div>
    <label>
      {title}
    </label>

    <Input value={value} onChange={onChange} />

    <style jsx>{`
      div {
        border-bottom: 1px solid rgba(0,0,0,0.13);
        margin: 0 -14px;
        padding: 0 14px;
      }
      div:first-of-type {
        border-top: 1px solid rgba(0,0,0,0.13);
      }
      label {
        font-size: 13px;
        color: rgba(81,81,112,0.66);
        text-transform: uppercase;
        display: block;
        margin-top: 8px;
      }
      input {
        width: 100%;
        box-sizing: border-box;
        font-size: 17px;
        padding-top: 8px;
        padding-bottom: 13px;
        border: none;
      }
      input:focus {
        border: none;
        outline: none;
      }
    `}</style>
  </div>
) 

export default class JoinForm extends React.Component {
  state = { dataKey: null, username:"", };

updateField = (fieldName, e) => {
  const newState = {}
  newState[fieldName] = e.target.value;

  this.setState(newState)
}

createUser = async (e) => {
  e.preventDefault() 

  // Some quick validation checks
  for (let key in this.state) {
    if (!this.state[key]) {
      return alert(`You must fill in your ${key}!`)
    }
  }

  const { username } = this.state

  try {
    // Open the MetaMask modal:
    await createUser(username)

    alert("Your user has been created!")
  } catch (err) {
    alert(`Sorry, we couldn't create your user: ${err}`)
  }
}


  
  render() {
   
  return (
    <div>
      <Form onSubmit={this.createUser}>
        <div className='join-form-dialog modal-body'>
        <p>Access the Crypto Asset Protocal to use a set of tools that help you take advantage passive investment stratigies</p>
        <InputForm 
          title="Desired username" 
          onChange={e => this.updateField("username", e)} 
        />  
        </div> 
        <div className="modal-footer">
          <JoinSubmitBtn drizzle={this.props.drizzle}  createUser = {this.createUser} />
        </div>
      </Form>
  
      <style jsx>
        {`  .join-form-btn {font-family: Poppins;letter-spacing:5.5px;}
            .join-form-dialog {font-family: Poppins; font-size:1rem;}
        `}                
      </style>
    </div>
    )
  }
}

//export default JoinForm;
