import React, { Component, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter, Button, Progress  } from "shards-react";
import Web3 from "web3";

const ContributeModal = ( {drizzleContext, assetData} ) => {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  return ( 
        <>
          <CardFooter className="p-0">
            <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Fund Purchase</button>
          </CardFooter>
          
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Fund Asset Purchase</Modal.Title>
            </Modal.Header>
            <Modal.Body>{assetData.token_id}
              {/* <Progress className='m-4' theme="primary" value={1} max={Web3.utils.fromWei(assetData.current_price,'ether')} />
              <div className="current-price">Current Price:{Web3.utils.fromWei(assetData.current_price,'ether')} ETH</div> */}
            </Modal.Body>
            <Modal.Footer>
              <Button variant="secondary" onClick={handleClose}>
                Close
                </Button>
              <Button variant="primary" onClick={handleClose}>
                Fund
              </Button>
            </Modal.Footer>
          </Modal>
        </>
         );    
        } 

    export default ContributeModal;
