import React from "react";
import { Link, Router } from '../../routes';
import { MetaMaskButton, EthAddress, Box, Text } from 'rimble-ui';
import RegModal from './RegModal.js';

import {
    Card,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Dropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem,
    InputGroup,
    InputGroupAddon,
    InputGroupText,
    FormInput,
    Button,
    Collapse
  } from "shards-react";

  
const customTheme = {
  colors: {
    green: '#17c671'
  },
  fontSizes: [1, 2, 3, 4, 5]
}


  export default class NavBar extends React.Component {
    constructor(props) {
      super(props);
  
      this.toggleDropdown = this.toggleDropdown.bind(this);
      this.toggleNavbar = this.toggleNavbar.bind(this);
  
      this.state = {
        dropdownOpen: false,
        collapseOpen: false,
        showRegisterModal: false,
      };
    }

    toggleRegisterModal = async () => {
      const { showRegisterModal } = this.state
  
      this.setState({
        showRegisterModal: !showRegisterModal,
      })
    }
  
    toggleDropdown() {
      this.setState({
        ...this.state,
        ...{
          dropdownOpen: !this.state.dropdownOpen
        }
      });
    }
  
    toggleNavbar() {
      this.setState({
        ...this.state,
        ...{
          collapseOpen: !this.state.collapseOpen
        }
      });
    }
    
   
    render() {
      const { showRegisterModal } = this.state
      return (   
      <div>
        <Navbar type="dark" theme="primary" expand="md">
          <NavbarBrand href="/">Crypto Asset Consortium</NavbarBrand>
          <NavbarToggler onClick={this.toggleNavbar} />
  
          <Collapse open={this.state.collapseOpen} navbar>
            <Nav navbar>
              <NavItem>
                <NavLink active href="/assetpool">
                  Interest Asset Pool
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink active href="#">
                  Interest Loan Protocol
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink active href="#">
                  Loan Funding Protocol
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink active href="#">
                  Compound Interest Protocol
                </NavLink>
              </NavItem>
            </Nav>
            <Nav className="ml-auto">
              <NavItem>
                
                <Button onClick={this.toggleRegisterModal} outline theme="success" size="md" className="my-2 mr-1">
                    JOIN
                </Button>
                             
                {showRegisterModal && (
                  <RegModal onClose={this.toggleRegisterModal} />
                )}
    
              </NavItem>
            </Nav>
            <Nav className="ml-auto">
              <NavItem>
              <Text theme={customTheme} color='green'>Hi, I'm magenta!</Text>
                <EthAddress theme={customTheme} color='green' fontSize={1} address="0x9505C8Fc1aD98b0aC651b91245d02D055fEc8E49" textLabels />
         
              </NavItem>
            </Nav>
          </Collapse>
        </Navbar>
        </div>
      );
    }
  }
