import React from "react";
import { newContextComponents } from "@drizzle/react-components";
import Web3 from "web3";

const { ContractData } = newContextComponents;

const UserInfo = ({ drizzle, drizzleState, accounts }) => {
  return (  

<div>
            <ContractData
              drizzle={drizzle}
              drizzleState={drizzleState}
              contract="UserStorage"
              method="profiles"
              methodArgs={this.props.drizzleState.accounts[0]} 
              render={displayData => {
              const values = Object.values(displayData);
              const userName = Web3.utils.toAscii(values[1]);
              //const userId = Web3.utils.toAscii(values[0]);
              <div>{userName}</div>
                        
                    }}
            />
             </div>
            )
 
           

}

export default UserInfo;
