import { Modal, Button, Form, FormGroup, FormInput } from "shards-react";

export default ({ onClose } ) => {


return (
     
  <div className="overlay">
    <div className="modal modal-dialog modal-dialog-centered" role="document">
      <div className="modal-content">
        <div className="modal-header">
          <h5 className="modal-title" id="exampleModalLongTitle">Join The Consortium</h5>
          <button onClick={onClose} type="button" className="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div className="modal-body">
        <Form>
          <FormGroup>
            <label htmlFor="#username">UserName</label>
            <FormInput id="#username" placeholder="UserName" />
          </FormGroup>
        </Form>
        </div>
        <div className="modal-footer">
          <button type="button" className="btn btn-secondary" data-dismiss="modal" onClick={onClose}>Close</button>
          <button type="button" className="btn btn-success" onClick={onClose} >JOIN</button>
        </div>
      </div>
    </div>
    
      <style jsx>{`
            .overlay {
              background-color: rgba(65,65,85,0.58);
              position: fixed;
              top: 0;
              bottom: 0;
              left: 0;
              right: 0;
              z-index: 110;
            }
            .modal {
              position: fixed;
              left: 50%;
              top: 50%;
              transform: translateX(-50%) translateY(-50%);
              width: 538px;
              border-radius: 3px;
              padding: 14px;
              z-index: 120;
            }
            .modal :global(h3) {
              text-align: center;
              margin: 10px 0;
            }
      `}</style>

  </div>
     














    );




  };
