import ContractManager from "../artifacts/contracts/ContractManager.json";
import UserStorage from "../artifacts/contracts/UserStorage.json";
import ProposalController from "../artifacts/contracts/ProposalController.json";
import UserController from "../artifacts/contracts/UserController.json";
import LANDRegistry from "../artifacts/contracts/LANDRegistry.json";
import Web3 from "web3";
import ManaToken from '../artifacts/contracts/ManaToken.json'

import Escrow from "../artifacts/contracts/Escrow.json";
import TwoEscrow from "../artifacts/contracts/TwoEscrow.json";

const newAbi = ManaToken.abi;
const landAbi = LANDRegistry.abi;

const escrowAbi = Escrow.abi
const TwoEscrowAbi = TwoEscrow.abi

var web3 = new Web3(Web3.givenProvider);

const drizzleOptions = {
  web3: {
    block: false,
    //customProvider:web3,
    fallback: {
      type: 'ws',
      url: 'ws://127.0.0.1:7545',
    },
  },
 
  contracts: [UserStorage, ContractManager, ProposalController, UserController,
 {
      contractName: 'MANAToken',
      web3Contract: new web3.eth.Contract(newAbi, '0x2BA2B6D42e950e141bbc0363D98f52117d5CBA17') 
    },
    {
      contractName: 'LANDRegistry',
      web3Contract: new web3.eth.Contract(landAbi, '0xecb86a0f5d6fd2B86Ad234182D6B188Aa6442Fae') 
    },
    {
      contractName: 'Escrow',
      web3Contract: new web3.eth.Contract(escrowAbi, '0xD7A0831B59b0828e963B3D42FFE34d225C746Ac0') 
    },
    

    {
      contractName: 'TwoEscrow',
      web3Contract: new web3.eth.Contract(TwoEscrowAbi, '0xebAed825C11dA919C173526C7591593D45D31981') 
    },

      

    //Roptsen Contracts
    // {
    //   contractName: 'MANAToken',
    //   web3Contract: new web3.eth.Contract(newAbi, '0x73d821F789597753831f2aB16eC64a71141437cc') 
    // },
    // {
    //   contractName: 'LANDRegistry',
    //   web3Contract: new web3.eth.Contract(landAbi, '0x3f27363c9f86E8ac151a0D332abC2C3b60B7bCf5') 
    // },
  ],

  events: {
    UserStorage: ["UserCreated"],
    ProposalController: ["AssetNomination"]
  },

  syncAlways:true,

  polls: {
    accounts: 1500,
  },


};

export default drizzleOptions;
