import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Accordion from 'react-bootstrap/Accordion';
import { Button } from "shards-react";

const NominationDetailsLink = ({ parcel, getTokenId, tokenIdState, txSuccessful }) => { 

  useEffect(() => {
  getTokenId()
});
 
return (
    <Accordion defaultActiveKey="1">
      <div> 
        <Accordion.Toggle as={Button}  variant="link" eventKey="0" outline theme="light">
        <div>Asset Details</div>
        </Accordion.Toggle>
          <Accordion.Collapse eventKey="0">
            <div className="p-3 mt-3 border rounded">
              <div>
              <div>
                  <Button onClick={getTokenId}>Token</Button>
              </div>
                <a href={`https://market.decentraland.org/contracts/0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d/tokens/`}  target="_blank">Decentraland</a>
              </div>
            </div>
          </Accordion.Collapse>
      </div> 
    </Accordion>
  )
}

const mapStateToProps = (state) => ({
  tokenIdState: state.tokenId,
  txSuccessful: state.txSuccessful,
  checkMetaMask: state.checkMetaMask,
  metaMaskReject: state.metaMaskReject
})

//working
//const getTokenId = () => ({ type: "TOKEN_ID_FETCHING", tokenId:11 });



//const getTokenId = (parcel) => ({ type: "GET_TOKEN_ID", parcel:parcel });

const mapDispatchToProps = (dispatch) => ({
  getTokenId: () => dispatch({ type: "TOKEN_ID_FETCH",tokenId:33 })
});

// const getTokenId = () => ({ type: "GET_TOKEN_ID" });

// const mapDispatchToProps = (dispatch) => ({
//   getTokenId: () => dispatch(getTokenId()),
// });

export default connect(mapStateToProps, mapDispatchToProps)(NominationDetailsLink);
