import { Box, Flex, Modal, Button, Text, Card } from "rimble-ui";

export default ({ onClose } ) => {

  
    return (
      <Box className="App" p={4}>
        <Box>
        {/* <Button onClick={openModal} outline theme="success" size="md" className="my-2 mr-1">JOIN</Button> */}
         
          {/* <Button onClick={openModal}>Open Modal</Button> */}
          <Modal isOpen={showRegisterModal}>
            <Card width={"420px"} p={0}>
              <Button.Text
                icononly
                icon={"Close"}
                color={"moon-gray"}
                position={"absolute"}
                top={0}
                right={0}
                mt={3}
                mr={3}
                onClick={onClose} 
              />
  
              <Box p={4} mb={3}>
                <Heading.h3>Join</Heading.h3>
                <Text>children</Text>
              </Box>
  
              <Flex
                px={4}
                py={3}
                borderTop={1}
                borderColor={"#E8E8E8"}
                justifyContent={"flex-end"}
              >
                <Button.Outline onClick={onClose} >Cancel</Button.Outline>
                <Button ml={3}>Confirm</Button>
              </Flex>
            </Card>
          </Modal>
        </Box>
      </Box>
    );




  };
