      const propAddress = await drizzle.contracts.ProposalController.methods["getPropTokenIdMap"](tokenId).call();
      store.dispatch({ type: 'GOT_PROP_ADDRESS', propAddress:propAddress});
      }
      return 'Loading...'
    }
  return next(action)
}
