import React from "react";
import { Button } from "shards-react";
import { Loader } from 'rimble-ui';

const NominateBtn = ({ drizzle, nominateAsset, intialContribution }) => {
 
  let state = drizzle.store.getState();
  let transactProcessing = state.dappReducer.transactionProcessing;
  let txSuccessful = state.dappReducer.txSuccessful
  //console.log(this.props.transactionReducer.checkMetaMask);
  if(!transactProcessing)  {
  //const transactionComplete = state.transactionReducer.transactionComplete;  
  return (

          <Button className="btn btn-block btn-success contribute-form-btn" key="submit" type="button" onClick={nominateAsset} onClose={txSuccessful} theme="success" >
            <p className="mb-0">Contribute&nbsp;&nbsp;{intialContribution}&nbsp;ETH</p>
          </Button>
        )   
  } 
  return (
          <div className="mx-auto">   
            <Loader color="#007bff" size="40px" />
          </div>
 
      )
}
export default NominateBtn;
