import { all, fork, takeLatest, put, call, take, race, select, takeEvery } from 'redux-saga/effects';
//import { toast } from 'react-toastify';
const delay = (ms) => new Promise(res => setTimeout(res, ms));

const TOKEN_ID_FETCH = 'TOKEN_ID_FETCH';
const GET_PROP_ADDRESS ='GET_PROP_ADDRESS'


const getDrizzle = (state) => state.dappReducer.drizzle;
const getContracts = (state) => state.contracts;

function* fetchTokenId (action) {
  yield put({ type: TOKEN_ID_FETCHED, tokenId:action.tokenId  });
}

function* fetchPropAddr (action) {
  //const propAddr = drizzle.contracts.ProposalController.methods.getPropTokenIdMap(action.tokenId).call();
  //const propAddr = 22;
  yield put({ type: 'GOT_PROP_ADDRESS', propAddress:22 });
  //yield call(delay, 200);
}


// function* txSuccessful() {
//   yield put({type:'TX_SUCCESSFUL'});
// }

// function* txProcessing() {
//   yield put({type:'TX_BROADCASTED'});
// }

export default function *appRootSaga() {
  //yield takeLatest('TX_BROADCASTED', txProcessing);
  //yield takeLatest('TX_SUCCESSFUL', txSuccessful);
  yield takeLatest('GET_PROP_ADDRESS', fetchPropAddr);
  yield takeLatest('TOKEN_ID_FETCH', fetchTokenId);
}
