import React from "react";
import { newContextComponents } from "@drizzle/react-components";
import { Button, Form } from "shards-react";

const { ContractForm } = newContextComponents;

const JoinForm = ({ onClose, drizzle, drizzleState }) => {
  return (
            <div>  
              <ContractForm
                drizzle={drizzle}
                drizzleState={drizzleState}
                contract="UserController"
                method="createUser" 
                fromAscii
                render={({ inputs, inputTypes, state, handleInputChange, handleSubmit }) => (
                  <Form onSubmit={handleSubmit}>
                   <div className='join-form-dialog modal-body'>
                     <p>Join the Consortium and gain access to tools that help you take advantage passive investment stratigies</p>
                    {inputs.map((input, index) => (
                      <input
                        style={{ fontSize: 30 }}
                        key={input.name}
                        type={inputTypes[index]}
                        name={input.name}
                        value={state[input.name]}
                        placeholder="Username"
                        onChange={handleInputChange}
                      />
                  ))}
                   </div> 
                   <div className="modal-footer">
                    {/* <Button className="btn-dark" onClick={onClose} type="button"  data-dismiss="modal" style={{ fontSize: 14 }} >
                      Close
                    </Button> */}
                    <Button className="btn btn-block btn-success" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 18 }} > 
                      JOIN CAP
                    </Button>
                  </div>
                  </Form>

                )}
              />
                <style jsx>{` .card-text {margin-bottom:.1rem;font-family: Poppins;}
                  .card {font-size: 10px;width:70%;margin-bottom:0;}
                  .card-header {font-family: Poppins;padding: .3rem 2.2rem;background-color: #f2f2f2;font-size:17px;font-weight:500;}
                  .card-body {padding: .6rem;font-size:17px;font-weight:400;} .joinButton {letter-spacing: 2.5px;}
                  .dia-total {color: #007bff;font-weight: 500;font-family: Poppins;letter-spacing: 1px;}
                  .dia-interest {color: #007bff;font-family: Poppins;font-weight: 500;letter-spacing: 1px;}`}                
                </style>
            </div>
          )
}

export default JoinForm;
