render() {

  const date = moment.unix(1584931427);
  const proposalEndTime = date.format("MM DD YYYY, HH:mm");

  return (  
    <div className="mb-4 d-flex justify-content-center">
      <div className="card">
        <div className="card-body">
          <Row className=" justify-content-center">
            <div className="col-sm my-auto inline-block d-flex justify-content-sm-center">
              Current Proposals
            </div> 
            <div className="col d-flex my-auto justify-content-center">
              <ProposalEndDate timeTillDate={proposalEndTime} />
            </div>  
            <div className="col-sm d-flex justify-content-sm-centerr">
              <Button className="my-auto" block outline theme="primary" size="sm">
                <div className="join-vote-btn">JOIN - VOTE</div>
              </Button>  
            </div>  
          </Row>
        </div>     
      </div>
        <style jsx>{` 
                  .card-text {margin-bottom:.1rem;font-family: Poppins;color:black;}
                  .card {width:70%;margin-bottom:0;}
                  .card-body {padding:.5rem;font-size:18px;font-weight:300;} 
                  .arrow {color:#17c671;font-size:21px;font-weight:900;}
                  `}  
        </style>
    </div>

        );
    }
}
