import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter, Button, Progress, Slider } from "shards-react";
import Web3 from "web3";
import InitialContribForm from '../../EthComponents/InitialContribForm';


const InitialProposalModal = ( {drizzleContext, assetData, state, handleSlide} ) => {
  
 

  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  //let value  = state.value;
  let valueSlider = state.valueSlider;
  let value  = 0;
  //let valueSlider = 0;

  //let contribAmount = state.contribAmount;
  // {console.log(state)}
  return ( 
  
        <>
          <CardFooter className="p-0">
            <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Fund Purchase</button>
          </CardFooter>
          
          <Modal show={show} onHide={handleClose}>
            <Modal.Header closeButton>
              <Modal.Title>Propose This Asset</Modal.Title>
            </Modal.Header>
            <Modal.Body>
            <div>
                  {assetData.sell_orders.map((subOne)=> 
                      {if(subOne.payment_token_contract.id === 1){
                        let maxRanger = Web3.utils.fromWei(subOne.current_price,'ether')
                        let maxRange = parseInt(maxRanger);
                        let minRange = 0;
                        let progressMax = maxRange-minRange;

                        return (
                        <div key={subOne.created_date.toString()}>
                          <div className="current-price">
                            <p className="mt-2 mb-1" >Current Price:&nbsp;{Web3.utils.fromWei(subOne.current_price,'ether')}&nbsp;ETH</p>
                          </div>
                          <div>
                          <p className="mt-4 mb-1" >Initial Contribution:</p>
                            <Slider
                              theme="success"
                              step={.01}
                              pips={{ mode: "range", stepped: true, density: 3 }}
                              onSlide={handleSlide}
                              connect={[true, false]}
                              start={[valueSlider]}
                              range={{ min: 0, max: progressMax }}
                            />
                          </div>  
                        {/* <div className="mt-4">{valueSlider} ETH </div>                  */}
                        </div>
                        )
                      }
                    }
                  )}
                </div>
            </Modal.Body>
            <div className="modal-footer">
              {/* <Button variant="secondary" onClick={handleClose}>
                Close
              </Button> */}
                <InitialContribForm state={state} valueSlider={valueSlider} drizzleContext = { drizzleContext } handleSlide={ handleClose } />
            </div>
          </Modal>
        </>
         );    
        } 

    export default InitialProposalModal;
