import React from 'react';
import { ContractData } from "@drizzle/react-components";

class EthAccounts extends React.Component {
 state = { dataKey: null };

 componentDidMount() {
   const { drizzle, drizzleState } = this.props;
   const contract = drizzle.contracts.UserStorage;
   const account = drizzleState.accounts[0];
   let dataKey = contract.methods["addresses"].cacheCall(account); // declare this call to be cached and synchronized
   this.setState({ dataKey });
 }

 render() {
   const { UserStorage } = this.props.drizzleState.contracts;
   const displayData = UserStorage.addresses[this.state.dataKey]; // if displayData (an object) exists, then we can display the value below
   return (
     <p>Hi from Truffle! Here is your storedData: {displayData && displayData.value}
     <ContractData contract="UserStorage" method="profiles" methodArgs={0} />
     </p>
   )
 }
}

export default EthAccounts

