import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import { Card, CardHeader, CardBody, CardFooter, CardTitle, Button, Row } from "shards-react";
import NominationModal from '../modals/NominationModal';
import FundingModal from '../modals/FundingModal.js';
import SaleModal from '../modals/SaleModal.js';
import SoldModal from '.PaidOutModal';
import PaidOutModal from '../modals/PaidOutModal.js';
import PurchaseModal from '../modals/PurchaseModal.js';
import NominationDetailsLink from '../NominationDetailsLink.js';
import Moment from "react-moment";



const CardFooterButton = ({ props, userManaBalance, getSoldStatus, nftAddress, expiresAt, assetPrice, tokenId, orderId, getOrderId, orderStatus, storedOrderId }) => {
  const [isStatus, setStatus] = useState(null);
  // const [isStoredOrderId, setStoredOrderId] = useState();
  console.log(storedOrderId)
  const fetchProposals = async () => {
    const proposal = await props.drizzle.contracts.ProposalController.methods.deployedProposals(`${tokenId}`).call();
    if(proposal){
      const proposalAddress = await props.drizzle.contracts.ProposalController.methods.proposalTokenIdMap(`${tokenId}`).call();
      const proposalBalance = await props.drizzle.contracts.MANAToken.methods.balanceOf(`${proposalAddress}`).call();
      const propData = await props.drizzle.contracts.ProposalController.methods.getPropData(`${proposalAddress}`).call();
      const status = propData[0];
      getOrderId(propData[2]);
      if(status === '4'){
        if(proposalBalance > assetPrice){
          const soldStatus = '6';
          getSoldStatus(proposalBalance, soldStatus);
          setStatus('6');
        }
          else{
            setStatus(status);
            getOrderId(orderId);
          }
      }
        else {
          setStatus(status);
          getOrderId(orderId);
        }
      }
      else{
        setStatus(null)
        getOrderId(orderId);
      }
    }

    useEffect(() => {
      fetchProposals();
    }, [tokenId]);

    return (
        <>
        {(() => {
        switch (isStatus) {
        //switch ('6') {
          //0-FUNDING
          case '0':
            return (
              <CardFooter className="p-0">  
                Funding:{orderId}
                <FundingModal 
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt}  
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter> 
            )
            // case '0'  && orderId === storedOrderId:
            //   return  (
            //     <>
            //       <CardFooter className="p-0"> 
            //       Nomination=0:{storedOrderId} 
            //         <NominationModal
            //           nftAddress={nftAddress}
            //           tokenId={tokenId}
            //           assetPrice={assetPrice}
            //           expiresAt={expiresAt} 
            //           props={props} 
            //           orderId={orderId}
            //           userManaBalance={userManaBalance}
            //           orderStatus={orderStatus}
            //         />
            //       </CardFooter> 
            //     </>
            // )
          //FUNDED
          case '1':
            return (
              <CardFooter className="p-0">
                <PurchaseModal 
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt} 
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter> 
            )
          //FOR_SALE
          case '4':
            return  (
              <CardFooter className="p-0">
                <SaleModal 
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt} 
                  props={props} 
                  userManaBalance={userManaBalance}
                />
              </CardFooter> 
            )
        //PAYINGOUT
          case '6':
            return  (
              <>
                <CardFooter className="p-0">
                  <SoldModal 
                    nftAddress={nftAddress}
                    tokenId={tokenId}
                    assetPrice={assetPrice}
                    expiresAt={expiresAt} 
                    props={props} 
                    userManaBalance={userManaBalance}
                  />
                </CardFooter> 
              </>
            )
            case '7':
              return  (
                <>
                  <CardFooter className="p-0">
                    <PaidOutModal
                      nftAddress={nftAddress}
                      tokenId={tokenId}
                      assetPrice={assetPrice}
                      expiresAt={expiresAt} 
                      props={props} 
                      userManaBalance={userManaBalance}
                    />
                  </CardFooter> 
                </>
            )
            case null :
              return  (
                <>
                  <CardFooter className="p-0">
                  null:{storedOrderId}
                    <NominationModal
                      nftAddress={nftAddress}
                      tokenId={tokenId}
                      assetPrice={assetPrice}
                      expiresAt={expiresAt} 
                      props={props} 
                      orderId={orderId}
                      userManaBalance={userManaBalance}
                      orderStatus={orderStatus}
                    />
                  </CardFooter> 
                </>
            )
          case 'error':
            return'error'
          default:
            return (
              <CardFooter className="p-0">
                default:{storedOrderId}
                <NominationModal
                  nftAddress={nftAddress}
                  tokenId={tokenId}
                  assetPrice={assetPrice}
                  expiresAt={expiresAt} 
                  props={props} 
                  orderId={orderId}
                  userManaBalance={userManaBalance}
                  orderStatus={orderStatus}
                />
              </CardFooter> 
            );
        }
      }
      )()}
    </>
    )
  }


  export default CardFooterButton;
