import React, { useState, useEffect } from 'react';
import Pagination from '../Pagination';
import Nominations from '../Nominations'
//import Moment from "react-moment";
import moment from "moment";




const NominatedCardsExpired = ({ props, userManaBalance, expiredAt, orderId }) => {
  
  const nowUnix = moment().valueOf();

  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [parcelsPerPage] = useState(6);
  const [totalOrders, setTotalOrders] = useState();
  const [graphqltest, setGraphql] = useState([]);
  
  //else if(propData[0] == 0 && propData[2] < nowUnix){
  const myHeaders = new Headers();
  myHeaders.append("Content-Type", "application/json");
 




  useEffect(() => {

    const fetchParcels = async () => {

      const proposalContract = await props.drizzle.contracts.ProposalController;
      const deployedAddresses = await proposalContract.methods.getDeployedAddresses().call();
      
      let i;
      let expiredOrderIdArray = [];
      console.log(expiredOrderIdArray);
      for (i = 0; i < deployedAddresses.length; i++) {
        const address = deployedAddresses[i];
        const propData = await proposalContract.methods.getPropData(`${address}`).call();  
      //const orderId = propData[3];
        let j;
        let orderId = ['0x011a8eb35862251cf992bc8006cd75195575b32001afd853b7f6e2d7ad36944d','0x06c0a3c24897a254c372cac998dcec1200a622d4745236c2d3ce1c162fb8ca8e','0x0af8c2510b5a68621e7af9a1e5df3d1340e6b000e0dbb2f96f201d5dc8510ca1'];
       
        for (j = 0; j < orderId.length; j++) {


        const graphql= JSON.stringify({ query: 
          `{
            orders(where: {id:"${orderId[j]}", status:cancelled, category:parcel }) {
              nft{
                parcel {
                  x,
                  y, 
                  tokenId
                }
                activeOrder {
                  price
                  expiresAt
                  nftAddress
                  txHash
                }
              }
            }
          }`
      })
  
      const targetUrl = 'https://api.thegraph.com/subgraphs/name/decentraland/marketplace'
      fetch(targetUrl, {
        method: "POST",
        headers:myHeaders,  
        body:graphql, variables:{},
        redirect: 'follow'
      })
        .then(res => res.json())
        .then( async (result) => {

          expiredOrderIdArray.push(result.orders.nft);
          //console.log(result)
          // const ordersGQL = await result.data.nfts;
          // const ordersFiltered = await result.data.nfts;
          // console.log(ordersFiltered)
          //setGraphql(result)
        },
        (error) => {
         return error;
        }
      ) 
    }
  }
    }
    fetchParcels();  
  }, []);


  // Get current orders
  const indexOfLastParcel = currentPage * parcelsPerPage;
  const indexOfFirstParcel = indexOfLastParcel - parcelsPerPage;
  const currentParcels = orders.slice(indexOfFirstParcel, indexOfLastParcel);

  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (   
    <>
      <Nominations 
        orders={currentParcels} 
        loading={loading} 
        props={props}   
        userManaBalance={userManaBalance}
      />
       <Pagination
        currentPage ={currentPage }
        parcelsPerPage={parcelsPerPage}
        totalParcels={totalOrders}
        paginate={paginate}
      />
    </>
  );
};


export default NominatedCardsExpired;
