import React from 'react';
import { connect } from 'react-redux';
import Accordion from 'react-bootstrap/Accordion';
import { Button, Row, Form } from "shards-react";
import Web3 from "web3";

const AwardAssetBtn =  ({ drizzle, drizzleState, propAddress, assetPrice }) => { 

const select = async () => {
  const account = await drizzleState.accounts[0];
  const contract = await drizzle.contracts[`${propAddress}`];
  
  try {
      contract.methods["weightContributors"].cacheSend({from:`${account}`});
    } catch (err) {
      console.error("Err:", err)
    }
  }
  return (
    <>
    <Row className=" mt-2 mb-1 justify-content-center">
    <div className="amount-text" ><span className="amount-number">{Web3.utils.fromWei(`${assetPrice}`,'ether')}</span> </div>
    <div className="amount-text" ><span className="original-amount-number"></span></div>
    </Row>
        <Row className=" mt-2 mb-1 justify-content-center">
          <div>
    
              <Row className="justify-content-center text-left">
                <Form onSubmit={select}>
                  <Button block theme="dark" className="mt-2 mx-auto"
                    key="submit"
                    type="button"
                    onClick={select} 
                    style={{ fontSize: 12 }}
                  >
                    Award NFT  
                  </Button>
                </Form>
              </Row>
          </div> 
          <style jsx> 
            {` 
              .amount-text {font-size:.8rem;color:#000;font-family: Poppins;}
              .amount-number {font-size:.8rem;color:#66a3ff;font-family: Poppins}
              .original-amount-number{font-size:.8rem;color:#c4183c;font-family: Poppins}
            `}    
          </style>
        </Row>
        </>
  )
}

const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice,
  assetPrice:state.appReducers.assetPrice
})

export default connect(mapStateToProps, null)(AwardAssetBtn);
