import React from "react";
import Web3 from "web3";
import { toast } from 'react-toastify';
import JoinSubmitBtn from '../components/buttons/JoinSubmit';
import { drizzleConnect, DrizzleContext } from '@drizzle/react-plugin';
import { Button } from "shards-react";
import PropTypes from 'prop-types';


const Input = ({ title, valueSlider, onChange }) => (
  <div>
    <label>
      {title}
    </label>
    <input className="form-control form-control-sm" value={valueSlider} onChange={onChange} />
    {console.log(value)}
  </div>
) 

class ContributeForm extends React.Component {
  // state = { 
  //   contribute:null,
  //   valueSlider:this.state.valueSlider
  // };


updateField = (fieldName, e) => {
  const newState = {}
  newState[fieldName] = e.target.value;
  this.setState(newState)
}

contribute = async (e) => {
  e.preventDefault() 

  for (let key in this.state) {
    if (!this.state[key]) {
      return toast.warning( `You must fill in your ${key}!`, { position: toast.POSITION.TOP_RIGHT });
    }
  }
  
    const { drizzle } = this.props;

    if(!userNameBool)  {
    const contract = drizzle.contracts.UserController;
    const uName = Web3.utils.fromAscii(username);

    try {
    contract.methods["contribute"].cacheSend(uName);
  } catch (err) {
    console.error("Err:", err)
  }
}
    else {
      return toast.warning( `Must enter valid amount`, { position: toast.POSITION.TOP_RIGHT });
    }
}

  render() {
    return (
      <div>
        <form onSubmit={this.contribute}>
          <div className="">
          <Input 
            title="Desired Contribution" 
            onChange={e => this.updateField("contribute", e)} 
          />  
          </div> 
          <div>
            <Button key="submit" type="button" onClick={this.contribute} variant="primary" onClick={this.props.handleClose}>
              Fund:<p>&nbsp;{this.props.state.valueSlider}&nbsp;ETH</p>
            </Button>
            {/* <JoinSubmitBtn drizzle={this.props.drizzle}  contribute = {this.contribute} /> */}
          </div>
        </form>
    
        <style jsx>
          {`  .join-form-btn {font-family: Poppins;letter-spacing:5.5px;}
              .join-form-dialog {font-family: Poppins; font-size:1rem;}
          `}                
        </style>
      </div>
    )
  }
  
}

export default ContributeForm; 



