import React, { useEffect, useState } from 'react';

const InterMediateForm = ({ drizzle, drizzleState, assetPrice, propAddress, tokenId }) => {     
  const contract = drizzle.contracts[`${propAddress}`];
  const [status, setProposalSatus] = useState(null);

    useEffect(() => {
      fetchProposalStatus();
    }, [propAddress])

    const fetchProposalStatus = async () => { 
      const dataKey = contract.methods['proposals'].cacheCall(tokenId);
      setProposalSatus(dataKey);
    }

    const propContracts = drizzleState.contracts[`${propAddress}`];

    0-FUNDING, 1-FUNDED, 2-WITHDRAWING, 3-PURCHASED, 4-FOR_SALE, 5-SOLD, 6-PAYINGOUT, 7-PAIDOUT, 8-EXPIRED, 9-SALE_FAILED, 10-AWARDED

    if(propContracts.proposals[status]) {
    const displayData = propContracts.proposals[status]; 
    const proposalStatus =  displayData && displayData.value.status;
    switch (proposalStatus) {
      //FUNDING
      case 0:
        return (
          <ContributeForm drizzle={drizzle} drizzleState={drizzleState} assetPrice={assetPrice} propAddress={propAddress} tokenId={tokenId} />
        )
      //FUNDED
      case 1:
        return  (
          <>
          <div className="modal-footer text-deposit-complete justify-content-center">
          ''
          </div>
          </>
        )
      //WITHDRAWING
      case 1:
        return  (
          <>
           <div className="mx-auto modal-footer justify-content-center"> 
            <p className="text-center">
              Withdrawal in progress...
            </p>  
            <Loader color="#007bff" size="40px" />
          </div>
          </>
        )
     //PURCHASED
      case 3:
        return  (
          <>
          <div className="modal-footer text-deposit-complete justify-content-center">
          'for sale'
          </div>
          </>
        )
      //FOR_SALE
      case 4:
      return  (
        <>
        <div className="modal-footer text-deposit-complete justify-content-center">
        'for sale'
        </div>
        </>
      )
      //SOLD
      case 5:
      return  (
        <>
        <div className="modal-footer text-deposit-complete justify-content-center">
        'for sale'
        </div>
        </>
      )
      //PAYINGOUT
      case 6:
      return  (
        <>
        <div className="modal-footer text-deposit-complete justify-content-center">
        'for sale'
        </div>
        </>
      )
      //PAIDOUT
      case 7:
        return  (
          <>
          <div className="modal-footer text-deposit-complete justify-content-center">
          'for sale'
          </div>
          </>
        )
      //EXPIRED  
      case 8:
        return  (
          <>
          <div className="modal-footer text-deposit-complete justify-content-center">
          'for sale'
          </div>
          </>
        )
      //SALE_FAILED  
      case 9:
        return  (
          <>
          <div className="modal-footer text-deposit-complete justify-content-center">
          'for sale'
          </div>
          </>
        )
      //AWARDED  
      case 10:
        return  (
          <>
          <div className="modal-footer text-deposit-complete justify-content-center">
          'for sale'
          </div>
          </>
        )     
      case 'error':
        return'error'
      default:
        return null;
    }
  }
  else {
    return null;
  }
}

export default InterMediateForm;
