import React from "react";
import { DrizzleContext } from "@drizzle/react-plugin";
import { Drizzle } from "@drizzle/store";
import options from "./drizzleOptions";
import { EthAddress, Loader } from 'rimble-ui';
import { newContextComponents } from "@drizzle/react-components";
import { BaseStyles, theme } from "rimble-ui";
import { ThemeProvider } from "styled-components";

const { AccountData } = newContextComponents;
const drizzle = new Drizzle(options);

const customTheme = {
  backgroundColor: {
    blue: '#007bff'
  }
};

const MyAddBar = () => {
  return (
    <DrizzleContext.Provider drizzle={drizzle}>
      <DrizzleContext.Consumer>
        {drizzleContext => {
          const {drizzle, drizzleState, initialized, status} = drizzleContext;

          if(!initialized) {
            return <Loader size="30px" color="white" />
          }
          return (

            <div >   
              <AccountData
                drizzle={drizzle}
                drizzleState={drizzleState}
                accountIndex={0}
                units="ether"
                precision={3}
                render={({ address, balance, units }) => (
                  console.log(drizzle.state.status),
                  <EthAddress theme={customTheme} backgroundColor='blue' address={address}  />
                
                )}
              />
            </div>
          )
        }}
      </DrizzleContext.Consumer>
    </DrizzleContext.Provider>
  );
}

export default MyAddBar;
