import React from 'react';
import { Link } from '../../routes';
import {
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  ButtonGroup,
  Button,
  Row,
  Col
} from "shards-react";

export default class AssetDash extends React.Component {

render() {
    return (     
      <Card small className="blog-comments">
        <CardHeader className="border-bottom">
          <h6 className="m-0">{title}</h6>
        </CardHeader>
    
        <CardBody className="p-0">
    
            <div className="blog-comments__item d-flex p-3">
              {/* Avatar */}
              <div className="blog-comments__avatar mr-3">
                <img alt="Hello" />
              </div>
    
              {/* Content */}
              <div className="blog-comments__content">
                {/* Content :: Title */}
                <div className="blog-comments__meta text-mutes">
                  <a className="text-secondary" href="/">
                    {discussion.author.name}
                  </a>{" "}
                  on{" "}
                  <a className="text-secondary" href={discussion.post.url}>
                    {discussion.post.title}
                  </a>
                  <span className="text-mutes">- {discussion.date}</span>
                </div>
    
                {/* Content :: Body */}
                <p className="m-0 my-1 mb-2 text-muted">{discussion.body}</p>
    
                {/* Content :: Actions */}
                <div className="blog-comments__actions">
                  <ButtonGroup size="sm">
                    <Button theme="white">
                      <span className="text-success">
                        <i className="material-icons">check</i>
                      </span>{" "}
                      Approve
                    </Button>
                    <Button theme="white">
                      <span className="text-danger">
                        <i className="material-icons">clear</i>
                      </span>{" "}
                      Reject
                    </Button>
                    <Button theme="white">
                      <span className="text-light">
                        <i className="material-icons">more_vert</i>
                      </span>{" "}
                      Edit
                    </Button>
                  </ButtonGroup>
                </div>
              </div>
            </div>
          ))}
        </CardBody>
    
        <CardFooter className="border-top">
          <Row>
            <Col className="text-center view-report">
              <Button theme="white" type="submit">
                View All Comments
              </Button>
            </Col>
          </Row>
        </CardFooter>
    </Card>
    );
}
}
