import React from 'react';
import Navbar from '../components/layout/Navbar.js';
import HeadOne from '../components/layout/Head.js';
import Footer from '../components/layout/Footer.js'
import { connect, Provider } from 'react-redux';
import { DrizzleContext, DrizzleProvider, drizzleConnect  } from "@drizzle/react-plugin"; 
import { ToastContainer } from 'react-toastify';   
import { generateStore } from "@drizzle/store";
import drizzleOptions  from "../EthComponents/drizzleOptions";
import { contractEventSaga, joinTransactionSaga } from '../middleware/capMiddleWare';
import rootSaga from '../middleware/sagas/fetchSaga';
import appSagas from '../middleware/sagas/rootSaga';
import reducer from '../middleware/reducers/appReducers.js'
import MyComponent from './innerInnerTestPage'
import PropTypes from 'prop-types'

class App extends React.Component {
  constructor(props, context) {
    super(props);
    // const { onSetDrizzleState } = this.props;
    {console.log(context)}
    this.props.getCurrentProp()
    // onSetDrizzleState(context.drizzle);

  }
  
  render() {
    
    return (
        <div className="App">{console.log(this.props.propAddress)}
          <div>hello</div>
          <MyComponent props={this.props} />
        </div>
   
    );
  }
}

App.propTypes = { 
  classes: PropTypes.object.isRequired,
};

App.contextTypes = {
  drizzle: PropTypes.object
}

const mapStateToProps = state => {
  return {
    propAddress: state.propAddress,
    drizzleStatus: state.drizzleStatus,
    //UserStorage: state.accounts[0]
  }
}

const mapDispatchToProps = dispatch => {
  return {
    getCurrentProp: () =>  dispatch({ type: "GET_PROP_ADDRESS",propAddress:2 }),
    onSetDrizzleState: (drizzle) => dispatch({ type: "SET_DRIZZLE_STATE", drizzle: drizzle })
  };
};

export default drizzleConnect(App, mapStateToProps, mapDispatchToProps);
