import React from "react";
import { Row,Button, Container  } from "shards-react";
import { Link } from '../../../routes';
import ProposalEndDate from '../component-overview/ProposalCountdown.js';
//import moment from 'moment';
import Moment from 'react-moment';
import Countdown from 'react-countdown';
//import ReactMomentCountDown from 'react-moment-countdown';



export default class ProposalBar extends React.Component {

  constructor(){
    super();
    this.state = {
      error:null,
      isLoaded:false,
      proposalEndTime:undefined,
      timeFormat:undefined,
      unixTimestamp:undefined
    }
  }


  componentDidMount() {
    this.getpoolStartTime();
  }

  getpoolStartTime(){
    // Ajax calls here
    const unixTimestamp=1584931427;
    this.setState({
      proposalEndTime:<Moment format="YYYY-MM-DDTHH:mm:ssZ" unix>{unixTimestamp}</Moment>
   
    });
  }
 
  // timeTillDate="03 22 2020, 7:43 pm"
  // timeFormat="YYYY-MM-DDThh:mm:ssZ" 
render() {
  const Completionist = () => unixTimestamp;
    return (  

    <div className="mb-4 d-flex ">
      <div className="card">
        <div className="card-body">
          <Row className="justify-content-center">
          <div className="col d-flex justify-content-center">
            <h6>  <Countdown date={unixTimestamp}>
            <Completionist />
    </Countdown></h6>  
          </div>  
          <div className="col d-flex justify-content-center">
            <ProposalEndDate  timeTillDate={this.state.proposalEndTime} />
          </div>  
          <div className="col d-flex justify-content-center">
            
          </div>  
          </Row>
        </div>     
      </div>
        <style jsx>{` 
                  .card-text {margin-bottom:.1rem;font-family: Poppins;color:black;}
                  .card {width:70%;margin-bottom:0;}
                  .card-body {padding:.5rem;font-size:18px;font-weight:300;} 
                  .arrow {color:#17c671;font-size:21px;font-weight:900;}
                  `}  
        </style>
    </div>

        );
    }
}
