import React from "react";
import { Button } from "shards-react";
import { Loader } from 'rimble-ui';

const JoinSubmitBtn = ({ drizzle, handleSubmit }) => {

  let state = drizzle.store.getState();
  const transactProcessing = state.transaction.transactionProcessing;
  console.log(transactProcessing)
  if(transactProcessing)  {
    
      return (
              <div className="mx-auto">
                <Loader color="#007bff" size="50px" />
              </div>
      )   
  }
  return (
    <div className="mx-auto">
    <Loader color="#007bff" size="40px" />
  </div>
    // <Button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
    //   ACCESS CAP
    // </Button>
  )
}
export default JoinSubmitBtn;
