import React, { Component, useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter, Button, Progress, Slider } from "shards-react";
import Web3 from "web3";
import CppContribSlider from '../sliders/CppContributeSlider';
import ContributeForm from '../forms/ContributeForm';


const ContributeModal = ( {drizzle, state, handleSlide, propAddress, tokenId, assetPrice} ) => {


  const [show, setShow] = useState(true);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  //let value  = state.value;
  let valueSlider = state.valueSlider;
  let maxRanger = `${assetPrice}`;
  let maxRange = parseInt(maxRanger);
  let minRange = 0;
  let progressMax = maxRange-minRange;

  return ( 
  
        <>
          {/* <CardFooter className="p-0">
            <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Fund Purchase</button>
          </CardFooter>
           */}
          {/* <Modal show={show} onHide={handleClose}> */}
            {/* <Modal.Header closeButton>
              <Modal.Title>Fund Asset Purchase</Modal.Title>   
            </Modal.Header>
            <Modal.Body>   */}
              <div>
                <div className="current-price">
                  <Progress value={minRange} max={`${assetPrice}`} >{minRange}</Progress>
                  <p className="mt-2 mb-1" >Current Price:&nbsp;{`${assetPrice}`}&nbsp;MANA</p>
                </div>
                <div>
                <p className="mt-4 mb-1" >Current Pool Depth: {minRange} MANA</p>
                  <Slider
                    theme="success"
                    step={.01}
                    pips={{ mode: "range", stepped: true, density: 3 }}
                    onSlide={handleSlide}
                    connect={[true, false]}
                    start={[valueSlider]}
                    range={{ min: 0, max: progressMax }}
                  />
                </div>  
                <div className="mt-4">{valueSlider} MANA </div>
                <div className="mt-4">Min. 100 MANA Contribution</div>                   
              </div>
            {/* </Modal.Body> */}
            <div className="modal-footer">
              <ContributeForm state={state} valueSlider={valueSlider} drizzle ={drizzle} handleSlide={handleClose} />
            </div>
          {/* </Modal> */}
        </>
         );    
        } 

    //export default ContributeModal;      


    export default class InitialContributeModal extends Component {
        constructor(props){
        super(props);
        this.handleSlide = this.handleSlide.bind(this),
     
        this.state = {
          error:null,
          isLoaded:false,
          value: 0,
          valueSlider:0,
          contribAmount:0,
          assetData: []
        }
      }
    

      handleSlide(e) {
        this.setState({
          valueSlider: parseFloat(e[0])
        });
      }

      render() {
        return (
        <ContributeModal
          propAddress={this.props.propAddress} 
          userManaBalance={this.props.serManaBalance}
          assetPrice={this.props.assetPrice} 
          drizzleState={this.props.drizzleState} 
          drizzle={this.props.drizzle} 
          tokenId={this.props.tokenId} 
          handleSlide={this.handleSlide} 
          state = {this.state}
        />
        )
      }

    }
