import { EventActions } from "@drizzle/store";
import { toast } from 'react-toastify';
import { put, takeEvery } from 'redux-saga/effects';

const GET_PROP_ADDRESS ='GET_PROP_ADDRESS';

export const joinTransactionSaga = store => next => action => {
  if (action.type === 'TX_SUCCESSFUL') { 
    const transactionHash = action.txHash
    const transactionNumber = `${transactionHash}`
    toast(transactionNumber, { position: toast.POSITION.BOTTOM_LEFT,className:'successful-transaction' })
  }
  return next(action)
}

   
export const contractEventSaga = lastSeenEventId => store => next => action => {
  if (action.type === EventActions.EVENT_FIRED) {
    if (action.event.id !== lastSeenEventId) {
      lastSeenEventId = action.event.id
       const message = action.event.returnValues._message
       const display = `${message}`
       toast.info(display, { position: toast.POSITION.TOP_RIGHT })
    }
  }
  return next(action)
}

export const fetchPropAddr = store => next => action => {
  if (action.type === GET_PROP_ADDRESS) {
    var state = store.getState()
    if (state.drizzleStatus.initialized) {
      // Declare this call to be cached and synchronized. We'll receive the store key for recall.
      //const contract = state.contracts.ProposalController;
      //const dataKey = contract.methods['getPropTokenIdMap'].cacheCall(action.tokenId)
      //console.log(contract.getPropTokenIdMap);
      //const tokenId = action.tokenId;
      const drizzle = action.drizzle;
      const contract = drizzle.contracts.ProposalController;
      //const account = drizzle.store.getState().accounts[0];
      //const contract = state.contracts.UserStorage;
   
      // Use the dataKey to display data from the store.
      //const propAddress = contract.methods['getPropTokenIdMap'].cacheCall(1);
      const propAddress = contract.methods["getPropTokenIdMap"].cacheCall('215792089237316195423570985008687907844762925492617102452873218822118924353538');
      
      console.log(propAddress);
      //store.dispatch({ type: 'GOT_PROP_ADDRESS', propAddress:propAddress});
      }
      return 'Loading...'
    }
  return next(action)
}
