import React from "react";
import UserInfo from "./UserInfo"; 
import MyAddBar from "./AddressBar.js"; 
import { DrizzleContext } from "@drizzle/react-plugin";
import { newContextComponents } from "@drizzle/react-components";
import { Drizzle } from "@drizzle/store";
import options from "./drizzleOptions";
import {  Loader } from 'rimble-ui';
import JoinButton from "../components/Buttons/JoinButton.js";

const { ContractData } = newContextComponents;
const drizzle = new Drizzle(options);

const AccountsContainer = () => {
 
  return  (
    <DrizzleContext.Provider drizzle={drizzle}>
      <DrizzleContext.Consumer>
        {drizzleContext => {
          const {drizzle, drizzleState, initialized } = drizzleContext;

          if(!initialized) {
            return <Loader size="30px" color="white" />
          }
          else  {
          const account = drizzleState.accounts[0];
          let member;
          //member = async (account) => {
          //const address =  
          <ContractData
            drizzle={drizzle}
            drizzleState={drizzleState}
            contract="UserStorage"
            method="addresses"
            methodArgs={[drizzleState.accounts[0]]}
            // render={displayData => {
            //   const value = displayData;
              
            //   <div>{value}</div>
                        
            //         }}
          />
          console.log(address)
          const  isMember = drizzle.contracts.UserStorage.methods.addresses(account).call();
          if(!isMember){
          
          return <JoinButton drizzleState={drizzleState} drizzle={drizzle} />
          }
          return <MyAddBar drizzleState={drizzleState} drizzle={drizzle}  />
          }
        }}
      </DrizzleContext.Consumer>
    </DrizzleContext.Provider>
);
  
}
export default AccountsContainer;
