import ContractManager from "../artifacts/contracts/ContractManager.json";
import UserStorage from "../artifacts/contracts/UserStorage.json";
import ProposalController from "../artifacts/contracts/ProposalController.json";
import UserController from "../artifacts/contracts/UserController.json";
import Web3 from 'web3';
import getWeb3 from "@drizzle-utils/get-web3";
import detectEthereumProvider from '@metamask/detect-provider';

const crap = async () => {
// this returns the provider, or null if it wasn't detected
const provider = await detectEthereumProvider();

if (provider) {
const web3Provider = new Web3(Web3.provider);
return web3Provider;
  
} else {
  console.log('Please install MetaMask!');
}

}

//const web3 = crap();
//const customProvider = new Web3.providers.HttpProvider(crap());
// console.log(web3);
// const host = "https://ropsten.infura.io/v3/5ee399b76fea4821a1fcc71a0ff87e60";
// const customProvider = new Web3.providers.HttpProvider(host);

const drizzleOptions = {
  web3: {
    type:'ws',
    url:'ws://127.0.0.1:9545'

    //customProvider:web3
  },

  contracts: [UserStorage, ContractManager, ProposalController, UserController],

  events: {
    UserStorage: ["UserCreated"],
    ProposalController: ["AssetNomination"]
  },
  polls: {
    accounts: 1500,
  },

  syncAlways:true,
};

export default drizzleOptions;
