import { all, fork, takeLatest, put, call, take, race, select, takeEvery } from 'redux-saga/effects';
import { toast } from 'react-toastify';

const TX_PROCESSING_DONE = "TX_PROCESSING_DONE";
const GOT_TOKEN_ID = 'GOT_TOKEN_ID';
 

function* txSuccessful() {
  console.log('hi')
}


function* fetchTokenId() {
  console.log('hello')
 }


function* appRootSaga() {
  yield takeEvery('TX_SUCCESSFUL', txSuccessful);
  yield takeEvery(GOT_TOKEN_ID, fetchTokenId)
}

export const appRootSagas = {
  appRootSaga
}

export default appRootSagas;
