import React from "react";
import { connect } from 'react-redux';
import { Button, Progress } from "shards-react";
import Web3 from "web3";
import { toast } from 'react-toastify';
import ManaTokenAbi from '../../artifacts/contracts/ManaTokenAbi.json'

const manaAbi = ManaTokenAbi;

const Input = ({ value, onChange, placeholder }) => (
  <div>
    <input className="form-control form-control-sm" value={value} onChange={onChange} placeholder={placeholder}  /> 
  </div>
) 

class ContributeForm extends React.Component {
  constructor(props){
    super(props);
    this.contribute = this.contribute.bind(this);
    this.state = {
      contribution:""
    }
  }

updateField = (fieldName, e) => {
  const newState = {}
  newState[fieldName] = e.target.value
  this.setState(newState)
}

contribute = async (e) => {
  e.preventDefault();
  for (let key in this.state) {    
    if (!this.state[key]) {
      return toast.warning(`You must fill in an amount ${key}!`,{ position: toast.POSITION.TOP_RIGHT })
    }
  }
  const { drizzle, drizzleState, propAddress, userManaBalance, assetPrice, proposalStorage, currentContribs } = this.props;
  const { contribution } = this.state;
  const account = drizzleState.accounts[0];
  const memberBool = await drizzle.contracts.UserStorage.methods.addresses(account).call();

  if(memberBool && propAddress !== null) {
  try {
    let web3 = new Web3(Web3.givenProvider);
    const manaContract = new web3.eth.Contract(manaAbi,'0x2BA2B6D42e950e141bbc0363D98f52117d5CBA17');
    const convertedContribution = web3.utils.toWei(`${contribution}`,'ether');
    manaContract.methods.approve(propAddress, convertedContribution).send({from:`${account}`, gas:100000});
    const currentContributions = await manaContract.methods.allowance(account, propAddress).call();
    currentContribs(currentContributions)
    .then(function(){
      proposalStorage.methods.contribute(`${contribution}`).send({from:`${account}`, gas:100000})
      })
       .catch(function(err) {
           console.log(err.message);
    });
   
  } catch (err) {
    return toast.error(`Sorry, contribution did not go through: ${err}`,{ position: toast.POSITION.TOP_RIGHT })
  }
}
}

  render() {
    const { drizzleState, assetPrice, userContribBalance } = this.props;
    let contractBalance = drizzleState.appReducers.contractBalance;
    let maxRanger = `${assetPrice}`;
    let minRange = contractBalance;
    console.log(userContribBalance)
    return (
      <div>
      <div className="contribution-modal">
        <div>
          <p className="mt-4 mb-1 text-center" >Current Pool Depth: {minRange} MANA</p>
          <Progress value={minRange}  max={maxRanger} ></Progress>
        </div>  
      </div>
      <div className="modal-footer justify-content-center">
        <form onSubmit={this.contribute}>
          <div className="contribute-form-dialog ">
          <Input 
              title="Contribute"
              placeholder="Minimium 100 MANA" 
              onChange={e => this.updateField("contribution", e)} 
            />
          </div> 
          <div>
            <Button className="mt-4 mb-1 btn btn-block btn-success contribute-form-btn" type="submit" onClick={this.contribute} theme="success" >
              <p className="mb-0">Contribute</p>
            </Button>
          </div>
        </form>
        </div>  
        <style jsx>
          {`  
          .contribution-modal{font-family: Poppins;}
          .min-amount-text {font-size:.7rem;}
          .mana-balance{font-size:.9rem;color:#007bff;}
          .contribute-form-btn {font-family: Poppins;letter-spacing:5.5px;}
          .contribute-form-dialog {font-family: Poppins; font-size:1rem;}
          `}                
        </style>
      </div>
    )
  }
  
}

//export default ContributeForm; 

const mapStateToProps = (state) => ({
  proposalStorage: state.appReducers.proposalStorage,
  userContribBalance: state.appReducers.userContribBalance
})

const mapDispatchToProps = (dispatch) => ({
  contractBalance: (contractBalance) =>  dispatch({ type: "PROPOSAL_BALANCE", balance: contractBalance }),
  currentContribs: (currentContributions) => dispatch({ type: "GOT_CURRENT_CONTRIB", userContribBalance: currentContributions }),
});


export default connect(mapStateToProps, mapDispatchToProps)(ContributeForm);
