import React, { Component, useEffect, useState } from 'react';
import { connect } from 'react-redux';
import Modal from 'react-bootstrap/Modal';
import { CardFooter, Button, Progress, Slider } from "shards-react";
import Web3 from "web3";
import CppContribSlider from '../sliders/CppContributeSlider';
import ContributeForm from '../forms/ContributeForm';
import ProposalStorageAbi from '../../artifacts/contracts/ProposalStorageAbi';


const ContributeModal =  ({ drizzleState, drizzle, state, handleSlide, propAddress, tokenId, assetPrice }) => {

const fetchProposalAddress = async () => {

const newAbi = ProposalStorageAbi;

let web3= new Web3("HTTP://127.0.0.1:7545");
if(propAddress === null) {
const proposalAddress = await drizzle.contracts.ProposalController.methods.getPropTokenIdMap(`${tokenId}`).call(); 
const contractConfig = {
  contractName: "ProposalStorage",
  web3Contract: new web3.eth.Contract(newAbi, `${proposalAddress}`)
  }
if (!drizzle.contracts['ProposalStorage']) {
  drizzle.addContract(contractConfig);
  } 
}

if(propAddress !== null){
  const contractConfig = {
    contractName: "ProposalStorage",
    web3Contract: new web3.eth.Contract(newAbi, `${propAddress}`)
  };
  if (!drizzle.contracts['ProposalStorage']) {
    drizzle.addContract(contractConfig);
  }
}
if(drizzle.contracts['ProposalStorage']){
const propBalance = await drizzle.contracts.ProposalStorage.methods.proposals(`${tokenId}`).call();
const proposalBalance = await propBalance.balance;
drizzle.store.dispatch({ type:'PROPOSAL_BALANCE', balance:`${proposalBalance}` });
  }
}

if(tokenId !== null) {
useEffect(() => {
  fetchProposalAddress();
}, [tokenId]);
}

  //let value  = state.value;
  // let contractBalance = drizzleState.appReducers.balance;
  // let valueSlider = state.valueSlider;
  // let maxRanger = `${assetPrice}`;
  // let maxRange = parseInt(maxRanger);
  // let minRange = contractBalance;
  // let progressMax = maxRange-minRange;

  return ( 
        <>
          <div className="contribution-modal">
            <div className="current-price">
              {/* <Progress value={minRange} max={maxRange} >{minRange}</Progress> */}
              {/* <p className="mt-2 mb-1" >Current Price:&nbsp;{`${assetPrice}`}&nbsp;MANA</p> */}
              {/* <p className=" mana-balance mt-2 mb-1" >Your MANA Balance:&nbsp;{`${userManaBalance}`}</p> */}
            </div>
            <div>
            {/* <p className="mt-4 mb-1" >Current Pool Depth: {minRange} MANA</p> */}
              {/* <Slider
                theme="success"
                step={.01}
                pips={{ mode: "range", stepped: true, density: 3 }}
                onSlide={handleSlide}
                connect={[true, false]}
                start={[valueSlider]}
                range={{ min: 0, max: progressMax }}
              /> */}
            </div>  
            <div className="mt-4">{valueSlider} MANA&nbsp;<span className="min-amount-text">(min. 100 MANA)</span></div>               
          </div>
          <div className="modal-footer">
            <ContributeForm state={state} valueSlider={valueSlider} drizzle={drizzle} drizzleState={drizzleState} handleSlide={handleSlide} />
          </div>
          <style jsx>
              {`  .contribution-modal{font-family: Poppins;}
                  .min-amount-text {font-size:.7rem;}
                  .mana-balance{font-size:.9rem;color:#007bff;}
              `}                
            </style>
        </>
         );    
      } 
   
    export default class InitialContributeModal extends Component {
      //   constructor(props){
      //   super(props);
      //   this.handleSlide = this.handleSlide.bind(this),
     
      //   this.state = {
      //     error:null,
      //     isLoaded:false,
      //     value: 0,
      //     valueSlider:0,
      //     contribAmount:0,
      //     assetData: []
      //   }
      // }
    

      // handleSlide(e) {
      //   this.setState({
      //     valueSlider: parseFloat(e[0])
      //   });
      // }

      render() {
        return (
        <ContributeModal
          propAddress={this.props.propAddress} 
          userManaBalance={this.props.userManaBalance}
          assetPrice={this.props.assetPrice} 
          drizzleState={this.props.drizzleState} 
          drizzle={this.props.drizzle} 
          tokenId={this.props.tokenId} 
          // handleSlide={this.handleSlide} 
          // state = {this.state}
        />
        )
      }
      

    }
