import React from 'react';
import Navbar from './Navbar.js';
import HeadOne from './Head.js';
import Footer from './Footer.js'
import options from "../EthComponents/drizzleOptions";
import { DrizzleContext } from "@drizzle/react-plugin"; 
import { ToastContainer } from 'react-toastify';
import { Drizzle } from "@drizzle/store";
//import store from '../EthComponents/middleware'

const drizzle = new Drizzle(options, store);


import { generateStore } from '@drizzle/store'
import drizzleOptions  from "../drizzleOptions";
import { contractEventSaga, joinTransactionSaga, appSagas } from '../EthComponents/middleware/capSagas';
import { appReducers } from '../EthComponents/middleware/capReducers';


const contractEventNotifier = contractEventSaga;

const appMiddlewares = [ contractEventNotifier ];

const store = generateStore({
  drizzleOptions,
  appMiddlewares,
  appReducers,
  appSagas,
  disableReduxDevTools: false 
})



//working store without Event Store
//const drizzleStore = generateStore(options);
//const drizzle = new Drizzle(options, drizzleStore);

export const Page = ({ children }) => (
  <div>
    <DrizzleContext.Provider drizzle={drizzle} store={store}>

     <HeadOne />
      <Navbar />  
      <script src="https://kit.fontawesome.com/bd21ab8235.js" crossOrigin="anonymous"></script>
    
      <main>
        <ToastContainer />
        {children}
      </main>
 
   
      <Footer />
    </DrizzleContext.Provider>
  </div>
) 

export const Center = ({ children }) => (
    <div>
      {children}   
    </div>
  )

