import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { newContextComponents } from "@drizzle/react-components";
import { Button, Progress, Row, Form } from "shards-react";
import Web3 from "web3";
import { Loader } from 'rimble-ui';
import { render } from '@testing-library/react';



// class AddrJoinNav extends React.Component {
//   state = { dataKey: null };
 
//  async componentDidMount() {
//    const { drizzle } = this.props;
//    let state = drizzle.store.getState();
//    const contract = drizzle.contracts.UserStorage;
//    const account = state.accounts[0];
 
//    try {
//    const dataKey = await contract.methods["addresses"].cacheCall(account);
//    this.setState({ dataKey });
//    }
//    catch (err) {
//      this.setState({ errorMessage: err.message });
//    }
//   }
 
//   componentDidUpdate(prevProps) {
//    const { drizzleState } = this.props;
//    if (prevProps.drizzleState.accounts[0] !== this.props.drizzleState.accounts[0]) {
//      window.location.reload();
//    }
//  }
 
//   render() {
//    const { UserStorage } = this.props.drizzleState.contracts;
//    const displayData = UserStorage.addresses[this.state.dataKey]; 
//    let user =  displayData && displayData.value;
 
//    if(user) {
//      return <AddressBar drizzle={this.props.drizzle} />;
//    }
//      return <JoinButton />;
//    }
//  }
 
//  export default AddrJoinNav;
 

//data.balance is 0 because it does not update until payout starts
const { ContractForm } = newContextComponents;

const SoldAsset = ({drizzle, drizzleState, propAddress, tokenId, listPrice, startPay, proposalBalance }) => {
  const account = drizzleState.accounts[0];
  const [deposits, setDespositInfo] = useState([]);
  const [reSalePrice, setReSalePrice] = useState(null);

  useEffect(() => {
    fetchProposalInfo();
  }, [])

  const fetchProposalInfo = async () => { 
      let state = drizzle.store.getState()
      if(state.drizzleStatus.initialized){
      const proposalContract = await drizzle.contracts[`${propAddress}`];
      const depositInfoLength = await proposalContract.methods.countDepositers().call();
      const propData = await proposalContract.methods["proposals"](`${tokenId}`).call();
      const convertedReSalePrice = Web3.utils.fromWei(`${propData.reSalePrice}`,'ether');
      setReSalePrice(convertedReSalePrice)

      let i;
      let depositerArray = [];
      for (i = 0; i < depositInfoLength; i++) {
        const depositInfo = await proposalContract.methods.depositers([i]).call();
        let depositerAmount = Web3.utils.fromWei(depositInfo.amount,'ether');
        let convertlistPrice = Web3.utils.fromWei(`${listPrice}`,'ether');
        let convertPropBalance = Web3.utils.fromWei(`${proposalBalance}`,'ether');
        let listPricePercent = ((depositerAmount / convertlistPrice) * 100).toFixed(0);
        let depositerPayOut = (convertPropBalance/100) * listPricePercent;
        depositerArray.push([i, depositInfo.depositer, depositerPayOut]);
        console.log(depositerAmount,convertlistPrice,convertPropBalance);
        console.log(depositInfo.amount,listPrice,proposalBalance);
        setDespositInfo(depositerArray);

    }
  }
   return console.log('Loading....')
  };;


  return(
    <>
        {deposits.length > 0 ?   
          <Row className="card-text justify-content-center mt-1"> 
            <div>
              <Row>
                <h5 className="mb-1">
                  Owners:
                </h5>
              </Row>
            {deposits.map(deposit => (
              <div className="deposit-info" key={deposit[0]} >
                <Row className="border-top"> 
                  <div>  
                    <p className="mb-1">
                      {deposit[1]} 
                    </p>
                    <p className="deposit-text text-center mb-1">
                      PayOut:&nbsp;<span className="purchase-price">{deposit[2]}</span>&nbsp;MANA
                    </p>
                    {/* <Progress className="mb-2" value={deposit[2]} max={Web3.utils.fromWei(proposalBalance,'ether')} ></Progress> */}
                  </div>
                </Row>
              </div>
            ))}
            </div>
            <style jsx>
              {` 
                .card-text{font-family:Poppins;font-size:1rem;}
                .purchase-price{font-size:1rem;color:#007bff;}
                .deposit-info{color:#000;font-weight:400;}
                .deposit-text{font-size:1rem;color:#000;}
              `}                
            </style>
          </Row>
        :
          <div className="align-items-center">   
            <Loader className="mx-auto" color="#007bff" size="40px" />
          </div>
        } 
      <ContractForm
        drizzle={drizzle}
        drizzleState={drizzleState}
        contract={propAddress}
        method="payOut" 
        sendArgs={{ from:account }}
        render={({ handleSubmit }) => (
          <Form onSubmit={handleSubmit}>
            <div className="modal-footer">
              <button className="btn btn-block btn-success join-form-btn" key="submit" type="button" onClick={handleSubmit} style={{ fontSize: 16 }} > 
                PayOut
              </button>
            </div>
          </Form>
        )}
      />
      <div>
        <p className="text-center progress-text mb-1">
          Total&nbsp;{reSalePrice}&nbsp;MANA
        </p>
        <Progress className="mb-1 mt-1" value={Web3.utils.fromWei(`${proposalBalance}`,'ether')}  max={reSalePrice}  ></Progress>
        <style jsx> {` 
          .sell-text{font-weight:400;color:black;font-size:1rem;font-family: Poppins;}
          .progress-text{font-weight:400;color:black;font-size:.9rem;font-family: Poppins;}
          .amount-number {color:#66a3ff;font-family: Poppins} 
          `}    
        </style>
      </div>
    </>
  )
}

const mapStateToProps = (state) => ({
  listPrice:state.appReducers.listPrice
})

export default connect(mapStateToProps, null)(SoldAsset);
