import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { CardFooter } from "shards-react";
import Web3 from "web3";
import NominationForm from '../forms/NominationForm';
 '../forms/InitialContribForm';


const NominationModal = ( { drizzleState, drizzle, assetData, state } ) => {
  let closeModal = drizzleState.dappReducer.txSuccessful;
  const [show, setShow] = useState(false);
  //const handleClose = () => setShow(false);
  const handleClose = () => setShow(closeModal);
  const handleShow = () => setShow(true);

  return (   
        <>{console.log(closeModal)}
          <button type="button" className="btn btn-lg btn-block card-footer-btn" onClick={handleShow}>Nominate Asset</button>
          {assetData.sell_orders.map((subOne)=> 
            {if(subOne.payment_token_contract.id === 1){
              const assetPrice = Web3.utils.fromWei(subOne.current_price,'ether');
              return (
                <Modal  key={subOne.created_date.toString()} show={show} onHide={handleClose}>
                  <Modal.Header closeButton>
                    <Modal.Title>Asset Price:&nbsp;{assetPrice}&nbsp;ETH</Modal.Title>
                    <p></p>
                  </Modal.Header>
                  <Modal.Body>
                    <div>
                      <div>
                        <div>
                        <NominationForm
                          state={state}                      
                          drizzle = { drizzle }
                          drizzleState = { drizzleState }  
                          assetPrice = {assetPrice}  
                          /> 
                        </div>  
                      </div>
                    </div>
                  </Modal.Body>
                </Modal>
                   )
                  }
                }
              )}
        </>
         );    
        } 

    export default NominationModal
  ;
