import React, {useState} from 'react';
import Accordion from 'react-bootstrap/Accordion';
import { Button } from "shards-react";


const NominationDetailsLink = ({ parcel }) => { 
  
//https://api.decentraland.org/v1/parcels/-25/2/bids?status=all
  const fetchDetailsLink = async () => {
    let proxyUrl = 'https://cors-anywhere.herokuapp.com/',
    targetUrl = 'https://api.decentraland.org/v1/parcels/' + x + '/' + y + '/bids?status=all'
    console.log(targetUrl)
    fetch(proxyUrl + targetUrl)
      .then(res => res.json())
      .then(
        (result) => {
          setParcels(result.data);
          setLoading(false); 
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
  ) 

  }
return (
    <Accordion defaultActiveKey="1">
      <div>  
        <Accordion.Toggle as={Button} onClick={fetchDetailsLink} variant="link" eventKey="0" outline theme="light">
        <div>Property Details</div>
        </Accordion.Toggle>
          <Accordion.Collapse eventKey="0">
          
            <div className="p-3 mt-3 border rounded">
              <div>
                <a  target="_blank">Decentraland</a>
              </div>
              <div>
                <a href={`https://market.decentraland.org/contracts/0xf87e31492faf9a91b02ee0deaad50d51d56d5d4d/tokens/115792089237316195423570985008687907844762925492617102452873218822118924353538`}  target="_blank">OpenSea</a>
              </div>
            </div>
          </Accordion.Collapse>
      </div> 
    </Accordion>
  )
}

export default NominationDetailsLink;
