import React from 'react';
import { ContractForm, AccountData, ContractData } from "@drizzle/react-components";
import Web3 from "web3"

class EthAccounts extends React.Component {
 state = { dataKey: null, userId: null };

 componentDidMount() {
   const { drizzle, drizzleState } = this.props;
   const contract = drizzle.contracts.UserStorage;
   //const account = drizzleState.accounts[0];
   

  //  let userId = drizzle.contracts.UserStorage.methods.addresses(account).call()
  //const dataKey = drizzle.contracts.UserStorage.methods.profiles([1]).call()
  //  const { username, id } = dataKey;
  const userId = contract.methods["addresses"].cacheCall(drizzleState.accounts[0]);

  const dataKey = async (userId,contract) => {
    const dataKey = contract.methods["profiles"].cacheCall(userId)
    return dataKey;
  }

  //const dataKey = contract.methods["profiles"].cacheCall(userId);
  // const dataKey = contract.methods["profiles"].cacheCall(1);
   this.setState({ dataKey });
 }

 render() {
  
   const { UserStorage } = this.props.drizzleState.contracts;
   const displayData = UserStorage.profiles[this.state.dataKey]; // if displayData (an object) exists, then we can display the value 
   console.log(this.state.dataKey)
  //  const profile = UserStorage.profiles[this.state.dataKey];

   
   //username: Web3.utils.toAscii(username),

   return (
    
     <p>Hi: {displayData && displayData.value[0]}</p>
   )
 }
}

export default EthAccounts

