    fetch(targetUrl,{header:'x-api-key:2f2a0900458f4bdfbb489680a58e7526'})
      .then(res => res.json())
      .then(
        (result) => {
          console.log(result)
          setPosts(result.asset_events);
          setLoading(false);
          
        },
        (error) => {
          setPosts({
            isLoaded: true,
            error
          });
        }
      ) 
    
      }

    fetchPosts();
  }, []);

  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
