import React, { Component } from 'react';
import { connect } from 'react-redux';
import ContributeInnerModal from './ContributeInnerModal.js';
import ProposalStorageAbi from '../../artifacts/contracts/ProposalStorage.json';

   
class InitialContributeModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      newAbi:null,
      approvedAmmount:null
    }
  }

 async componentDidMount() {   
    const { getPropAbi,gotApprovedBalance, drizzle, drizzleState, propAddress } = this.props;
    getPropAbi(ProposalStorageAbi.abi)

    const account = drizzleState.accounts[0];
    const manaContract = drizzle.contracts.MANAToken; 
    const approvedAmmount = await manaContract.methods.allowance(account,propAddress).call();
    gotApprovedBalance(approvedAmmount);
  }
  
  render() {
    return (
      <ContributeInnerModal
        props = {this.props}
      />
    )
  }
}   

const mapDispatchToProps = (dispatch) => ({
  getPropAbi: (ProposalStorageAbi) =>  dispatch({ type: "GOT_PROP_ABI", proposalStorageAbi:ProposalStorageAbi }),
  gotApprovedBalance: (approvedAmount) => dispatch({ type:'APROVED_AMOUNT', approvedAmount:approvedAmount }),
});

export default connect(null, mapDispatchToProps)(InitialContributeModal);
